import React, { Component } from 'react'
import Layout from "./Layout";
import { urlOfImages } from "./images";
import fetch from 'isomorphic-fetch';
import { Router } from 'next/router';

class Query extends Component{
    constructor()
    {
        super();
        this.state = {
            questionList :[],
            answers : [],
            questionBox : false
        }
        
    }
      componentDidMount() {
            fetch('http://localhost:8000/questions/')
            .then(response => response.json())
            .then((res)=>{
                this.setState( {questionList : [...res.questionList]});
            })
            
      }
      renderQuestion(){

          return (<>
              { this.state.questionList.map((question)=>{
                   return <div className="text-4xl bg-red-500 text-white p-8 my-4 mx-4 rounded hover:bg-red-600" onClick={() => console.log(question._id)}>
                  <b>Q.</b>   {question.question}
                  
              </div>
              })}
          </>
          );

      };


      openQuestionDialog(){
          this.setState({
              questionBox : !this.state.questionBox
          })
      };

      
    render(){
    return(
        <Layout>
           <div className={this.state.questionBox ? 'hidden':'block'}>
            <div className="flex overflow-hidden">
                <div className="fixed bottom-3 right-3 md:block  p-0 md:p-8">
                    <div className=" w-full bg-red-300 hover:bg-red-700 text-white font-bold   p-2  rounded ">
                        <img src={urlOfImages.question} className="h-8 rounded-full inline-block" onClick={()=>{this.openQuestionDialog()}}/><sapn className="hidden md:block " > Ask A Question</sapn>
                    </div>
                    
                    
                </div>
                
            <div className="block text-center  border-gray-600 w-screen ">
                
                {this.renderQuestion()}
              </div>
              </div>  
              </div>
            <div className={this.state.questionBox ? 'block':'hidden'}>
                <center>
              <div className="max-w-sm rounded overflow-hidden shadow-lg mt-10 border">
 
  <div className='px-4 py-6'>
    <div className="font-bold text-xl mb-2">Write Your Question Here</div>
    
    <textarea class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="username" placeholder="Enter Your question Here ..." rows="6"/>
    <button class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
  Submit Question
</button>
  </div>
  
</div>
</center></div>
        </Layout>
        
    )
};
}

export default Query;
