import React, { Component } from 'react'
import Layout from "./Layout";
import { urlOfImages } from "./images";
import fetch from 'isomorphic-fetch';

class Query extends Component{
    constructor()
    {
        super();
        this.state = {
            questionList :[],
            answers : []
        }
        
    }
      componentDidMount() {
            fetch('http://localhost:8000/questions/')
            .then(response => response.json())
            .then((res)=>{
                this.setState( {questionList : [...res.questionList]});
            })
            
      }
      renderQuestion(){

          return (<>
              { this.state.questionList.map((question)=>{
                   return <div className="text-4xl bg-red-500 text-white p-8 my-4 mx-4 rounded hover:bg-red-600" onClick={() => console.log(question._id)}>
                  {question.question}
                  
              </div>
              })}
          </>
          );

      };
    render(){
    return(
        <Layout>
           
            <div className="flex overflow-hidden">
                <div className="hidden md:block w-1/6 p-8">
                    <div className=" w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded m-4">
                        Ask A Question
                    </div>
                    <div className="bg-blue-500 w-full hover:bg-blue-700 text-white font-bold py-2 px-4 rounded m-4">
                        Top
                    </div>
                    <div className="bg-blue-500 w-full hover:bg-blue-700 text-white font-bold py-2 px-4 rounded m-4">
                        Bottom
                    </div>
                    
                </div>
            <div className="block text-center  border-gray-600 w-screen md:w-5/6">
                
                {this.renderQuestion()}
              </div>
              </div>  
                
        </Layout>
        
    )
};
}

export default Query;
