import React, { Component } from 'react'
import Layout from "./Layout";
import { urlOfImages } from "./images";
import fetch from 'isomorphic-fetch';

class Query extends Component{
    constructor()
    {
        super();
        this.state = {
            questionList :[],
            answers : []
        }
        
    }
      componentDidMount() {
            fetch('http://localhost:8000/questions/')
            .then(response => response.json())
            .then((res)=>{
                this.setState( {questionList : [...res.questionList]});
            })
            
      }
      renderQuestion(){

          return (<>
              { this.state.questionList.map((question)=>{
                   return <div className="text-4xl bg-red-500 text-white p-8 my-4 mx-4 rounded hover:bg-red-600" onClick={() => console.log(question._id)}>
                  {question.question}
                  
              </div>
              })}
          </>
          );

      };

      renderAnswer(question){
          if(question.answers && question.answers.length == 0 )
          {
              return (<div className="text-2xl bg-green-500 p-8 text-white">
              Answers
              <div className="text-4l">
                  No One Has Answered
              </div>
              </div>)
          }
          else{
            fetch('http://localhost:8000/questions/'+question._id)
            .then(response => response.json())
            .then((res)=>{
                console.log(res);
                this.setState( {answers : [res.question.answers]});
            })
              return (
                <div className="text-2xl bg-green-500 p-8 text-white">
                Answers
                
                {this.state.answers.map((answer) => {
                    <div className="flex justify-center bg-green-300 p-4 m-4">
                    <div className="block md:flex justify-center w-full">
                    <div><center>
                        <img src={urlOfImages.answer} className="h-6 mt-5"/></center>
                    </div>
                    
                    <div className='p-1 w-auto text-4m md:text-2xl'>
                        {answer.answer}
                        <span className="ml-2">
                       wdjbjd{answer.answer}
                       </span>
                       {/* <div className="text-right text-gray-500">-- {answer.author.name}</div> */}
                    </div>
                    {/* <div className="px-4 mx-6">
                    <button className="bg-green-500 hover:bg-green-700 w-full text-white p-2 px-4 mt-1 rounded mr-4">Upvote {answer.upvote.length}</button> 
                    
                    <button className="bg-red-500 hover:bg-red-700 text-white w-full rounded px-4 p-2 mt-1 ">Downvote {answer.downvote.length}</button> 
                    </div> */}
                    </div>
                    </div>
                })}
                </div>
              )
          }
      }
    render(){
    return(
        <Layout>
           
            <div className="flex overflow-hidden">
                <div className="hidden md:block w-1/6 p-8">
                    <div className="bg-blue-500 hover:bg-blue-700 w-full text-white font-bold py-2 px-4 rounded m-4">
                        Answer
                    </div>

                    <div className=" w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded m-4">
                        Ask A Question
                    </div>
                    <div className="bg-blue-500 w-full hover:bg-blue-700 text-white font-bold py-2 px-4 rounded m-4">
                        Top
                    </div>
                    <div className="bg-blue-500 w-full hover:bg-blue-700 text-white font-bold py-2 px-4 rounded m-4">
                        Bottom
                    </div>
                    
                </div>
            <div className="block text-center  border-gray-600 w-screen md:w-5/6">
                
                {this.renderQuestion()}
              </div>
              </div>  
                
        </Layout>
        
    )
};
}

export default Query;
