import Social from '../signupAndLogin/social';
import Layout from '../Layout';
import { render } from 'react-dom';
import { Component } from 'react';



class LogIn extends Component
{
  
  constructor(){
    super(props);
    this.state = {
      email:'',
      password:''
    }
  }
  
  getEmail(val){
    this.setState({email:val.target.value});
  }

  getPassword(val){
    this.setState({password:val.target.value});
  }

  render(){

                return (<>
                      <Layout>
                        <div>
                        <Social />
                        <center>
                        <div className="mt-5 border w-full max-w-xs">
                  <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
                    <div className="mb-4">
                      <label className="block text-gray-700 text-sm font-bold mb-2" for="username">
                        Email
                      </label>
                      <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="username" type="text" placeholder="Email" required/>
                    </div>
                    <div className="mb-6">
                      <label className="block text-gray-700 text-sm font-bold mb-2" for="password">
                        Password
                      </label>
                      <input  className="shadow appearance-none border  rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline" id="password" type="password" placeholder="Password" required/>
                    </div>
                    <div className="flex items-center justify-between">
                      <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" type="button">
                        Log In
                      </button>
                      <a className="inline-block align-baseline font-bold text-sm text-blue-500 hover:text-blue-800" href="#">
                        Forgot Password?
                      </a>
                    </div>
                  </div>
                
                </div>
                </center>
                </div>
                </Layout>
                </>
                );
            };
}
export default LogIn;
